import React, { useEffect, useState } from "react";

const API_URL = "http://localhost:3000/api/services"; // твой Node API

function App() {
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState(null);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    fetch(`${API_URL}/services)`)
      .then(res => res.json())
      .then(data => setServices(data))
      .catch(console.error);
  }, []);

  useEffect(() => {
    if (!selectedService?.products) return;
    const sum = selectedService.products
      .filter(p => selectedProducts.includes(p.id))
      .reduce((acc, p) => acc + Number(p.price), 0);
    setTotalPrice(sum);
  }, [selectedProducts, selectedService]);

  const loadService = (id) => {
    fetch(`${API_URL}/services/${id}`)
      .then(res => res.json())
      .then(data => {
        setSelectedService(data);
        setSelectedProducts([]);
      })
      .catch(console.error);
  };

  const toggleProduct = (id) => {
    setSelectedProducts(prev =>
      prev.includes(id) ? prev.filter(p => p !== id) : [...prev, id]
    );
  };

  const createOrder = () => {
    if (!selectedService) return;
    fetch(`${API_URL}/orders`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        serviceId: selectedService.service.id,
        products: selectedProducts
      })
    })
      .then(res => res.json())
      .then(data => alert(`Заказ создан! Сумма: ${data.order.totalPrice}`))
      .catch(console.error);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Конструктор услуг</h1>

      <h2>Список услуг</h2>
      {services.map(s => (
        <button key={s.id} onClick={() => loadService(s.id)} style={{ margin: "5px" }}>
          {s.name}
        </button>
      ))}

      {selectedService && (
        <div style={{ marginTop: "20px" }}>
          <h2>{selectedService.service.name}</h2>

          {selectedService.products.map(p => (
            <div key={p.id}>
              <label>
                <input
                  type="checkbox"
                  checked={selectedProducts.includes(p.id)}
                  onChange={() => toggleProduct(p.id)}
                />
                {p.name} — ${p.price}
              </label>
            </div>
          ))}

          <h3>Итого: ${totalPrice}</h3>
          <button onClick={createOrder} style={{ marginTop: "10px" }}>Оформить заказ</button>
        </div>
      )}
    </div>
  );
}

export default App;
